windowclass.script:
function onInit()
	if super and super.onInit then
		super.onInit();
	end

	if DataCommon and DataCommon.pseudoconditions and (#(DataCommon.pseudoconditions) > 0) then
		for _,sCondition in ipairs(DataCommon.pseudoconditions) do
			local w = conditions_list.createWindow();
			w.setEffect({ sName = StringManager.capitalize(sCondition) });
		end
	else
		pseudoconditions_label.setVisible(false);
		pseudoconditions_list.setVisible(false);
	end
end
